import React, { Component } from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import './App.css';
import BaseRouter from './routes';
import Layout from './Common/Layout.js';
import 'antd/dist/antd.css';
import QueueAnim from 'rc-queue-anim';
import "bootstrap/dist/css/bootstrap.min.css";
import { OverPack } from 'rc-scroll-anim';
import Login from "./Components/login.component";
import Register from "./Components/register.component";
import Home from "./Components/home.component";
import Profile from "./Components/profile.component";
import BoardUser from "./Components/board-user.component";


class App extends Component {
    constructor(props) {
        super(props);
        this.logOut = this.logOut.bind(this);

        this.state = {
            showModeratorBoard: false,
            showAdminBoard: false,
            currentUser: undefined,
        };
    }

    contactUsDiv = () => {
        return (
                <QueueAnim style={{ backgroundColor: "#ffffff" }}>
                    <div key="a" style={{ marginTop: "80vh", align: "bottom", padding: "2%", fontWeight: "bold", fontSize: "large" }}>
                        Contact us
                    </div>
                    <div key="b" style={{ paddingLeft: "10%", paddingRight: "10%", display: "flex" }}>
                        <div style={{ float: "right", fontSize: "larger", textAlign: "center", width: "60vw"}} >
                            Phone: XXX-XXX-XXX
                            <br />
                            Email: xxx@email.com
                            <br />
                        </div>
                    </div>
                </QueueAnim>
        );
    }

    componentDidMount() {
        const user = AuthService.getCurrentUser();

        if (user) {
            this.setState({
                currentUser: user,
                showModeratorBoard: user.roles.includes("ROLE_MODERATOR"),
                showAdminBoard: user.roles.includes("ROLE_ADMIN"),
            });
        }
    }

    logOut() {
        AuthService.logout();
    }

    render() {
        const { currentUser, showModeratorBoard, showAdminBoard } = this.state;
        return (
            <div className="App" style={{height: "100%"}}>
                <Router>
                    <div>
                        <nav className="navbar navbar-expand navbar-dark bg-dark">
                            <Link to={"/"} className="navbar-brand">
                                bezKoder
                            </Link>
                            <div className="navbar-nav mr-auto">
                                <li className="nav-item">
                                    <Link to={"/home"} className="nav-link">
                                        Home
                                    </Link>
                                </li>

                                {showModeratorBoard && (
                                    <li className="nav-item">
                                        <Link to={"/mod"} className="nav-link">
                                            Moderator Board
                                        </Link>
                                    </li>
                                )}

                                {showAdminBoard && (
                                    <li className="nav-item">
                                        <Link to={"/admin"} className="nav-link">
                                            Admin Board
                                        </Link>
                                    </li>
                                )}

                                {currentUser && (
                                    <li className="nav-item">
                                        <Link to={"/user"} className="nav-link">
                                            User
                                        </Link>
                                    </li>
                                )}
                            </div>

                            {currentUser ? (
                                <div className="navbar-nav ml-auto">
                                    <li className="nav-item">
                                        <Link to={"/profile"} className="nav-link">
                                            {currentUser.username}
                                        </Link>
                                    </li>
                                    <li className="nav-item">
                                        <a href="/login" className="nav-link" onClick={this.logOut}>
                                            LogOut
                                        </a>
                                    </li>
                                </div>
                            ) : (
                                <div className="navbar-nav ml-auto">
                                    <li className="nav-item">
                                        <Link to={"/login"} className="nav-link">
                                            Login
                                        </Link>
                                    </li>

                                    <li className="nav-item">
                                        <Link to={"/register"} className="nav-link">
                                            Sign Up
                                        </Link>
                                    </li>
                                </div>
                            )}
                        </nav>

                        <div className="container mt-3">
                            <Switch>
                                <Route exact path={["/", "/home"]} component={Home} />
                                <Route exact path="/login" component={Login} />
                                <Route exact path="/register" component={Register} />
                                <Route exact path="/profile" component={Profile} />
                                <Route path="/user" component={BoardUser} />
                                <Route path="/mod" component={BoardModerator} />
                                <Route path="/admin" component={BoardAdmin} />
                            </Switch>
                        </div>
                    </div>
                    );
                    }
                    }
                      <div className="Base" >
                              <div className="BaseInfo">
                               <BaseRouter />
                              </div>
                      </div>
                      <this.contactUsDiv />

                </Router>

            </div>);
    }

}
export default App;
