import  React, { Component } from  'react';
import './PlatformList.css';
import Filter from './Filter';
import PropTypes from 'prop-types';
// import './pictures/car_no_image.jpg';
// import  PlatformService  from './PlatformService';
// const  platformService  =  new PlatformService();

class PlatformList extends Component {

    constructor() {
        super();
        this.state = {
            platform: [],
        };
        const{ data: { year_min, year_max, price_min, price_max },
            errors,
            handleInput,
            handleSubmit,
            } = this.props;
    }

    componentDidMount() {
        var url = "http://127.0.0.1:8000/api/platforml/price_diff=10"  // + this.props.product

        fetch(url, {
            mode: 'cors',
            method: 'GET',
        })
            .then(results => {return results.json()}).then(data => {
            let platform = data.results.map((plat) => {
                return (
                    <span key={plat.id} onClick={ e => window.location.href = plat.ad_link } >
                    <div className="AdContainer"  >
                        <div className="imageContainer">
                            <img src={plat.photo_link} alt="" />
                        </div>
                        <div className="titleContainer">
                            <p><b>{plat.brandId}  {plat.model}</b></p>
                            <p> {plat.year}</p>
                        </div>
                        <div className="priceContainer">
                                <div className="price">Price: {plat.price} EUR</div>
                                <div className="price_diff"> â‚¬ {plat.price_diff} </div>
                        </div>
                    </div>
                    </span>
                )
            })
            this.setState({platform: platform})
        })

    }

    render() {
        return( <div className="FilterContainer">
                <form action="http://127.0.0.1:8000/api/platforml/price_diff=10" method="get">
                    <div>
                        <label htmlFor="year_min">Year from</label>
                        <input name="year_min" id="year_min" value="2000"/>
                        <label htmlFor="year_max">to</label>
                        <input name="year_max" id="year_max" value="2021"/>

                        <label htmlFor="price_min">Price from</label>
                        <input name="price_min" id="price_min" value="0"/>
                        <label htmlFor="pricer_max">to</label>
                        <input name="pricer_max" id="price_max" value="500000"/>

                        <button>Search</button>
                    </div>
                </form>
                {this.state.platform}
        </div>

    )}
}

Filter.propTypes = {
    data: PropTypes.shape({
        price_min: PropTypes.string.isRequired,
        price_max: PropTypes.string.isRequired,
        year_min: PropTypes.string.isRequired,
        year_max: PropTypes.string.isRequired,
    }).isRequired,

    handleInput: PropTypes.func.isRequired,
    handleSubmit: PropTypes.func.isRequired,
};

export  default  PlatformList;
