import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
// import ScotchInfoBar from './ScotchInfoBar';
// import './styles.css';

function App() {
  const [platform, setPlatform] = useState(null);

  const fetchData = async () => {
    const response = await axios.get(
      'http://127.0.0.1:8000/api/platform/'
    );

    setPlatform(response.data);
    console.log(response.data);
  };

  return (
    <div className="App">
      <h1>Game of Thrones Books</h1>
      <h2>Fetch a list from an API and display it</h2>

      {/* Fetch data from API */}
      <div>
        <button className="fetch-button" onClick={fetchData}>
          Fetch Data
        </button>
        <br />
      </div>

      {/* Display data from API */}
      <div className="books">
        {platform &&
          platform.map((plat, index) => {
            const cleanedDate = new Date(plat.brandId).toDateString();
            const model = plat.model.join(', ');

            return (
              <div className="plat" key={index}>
                <h3>BrandId {index + 1}</h3>
                <h2>{platform.brandId}</h2>

                <div className="details">
                  <p>üë®: {model}</p>
                  <p>üìñ: {platform.year} year</p>
                  <p>üèòÔ∏è: {platform.price} EUR</p>
                  <p>‚è∞: {cleanedDate}</p>
                </div>
              </div>
            );
          })}
      </div>

      {/*<ScotchInfoBar seriesNumber="7" />*/}
    </div>
  );
}

const rootElement = document.getElementById('root');
ReactDOM.render(<App />, rootElement);
