import  React, { Component } from  'react';
import  PlatformService  from './PlatformService';
const  platformService  =  new PlatformService();
class PlatformList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            platform: [],
            nextPageURL:  ''
        };
        this.nextPage = this.nextPage.bind(this);
        // this.handleDelete  =  this.handleDelete.bind(this);
    }



componentDidMount()
{
    var self = this;
    platformService.getPlatform().then(function (result) {
        self.setState({ platform:  result.data, nextPageURL:  result.nextlink})
    });
}

// handleDelete(e,pk)
// {
//     var  self  =  this;
//     usersService.deleteUser({pk :  pk}).then(()=>{
//         var  newArr  =  self.state.users.filter(function(obj) {
//             return  obj.pk  !==  pk;
//         });
//
//         self.setState({users:  newArr})
//     });
// }

nextPage()
{
    var self  =  this;
    console.log(this.state.nextPageURL);
    platformService.getPlatformByURL(this.state.nextPageURL).then((result) => {
        self.setState({ ads:  result.data, nextPageURL:  result.nextlink})
    });
}
render() {

    return (
        <div className="platform--list">
            <table className="table">
                <thead key="thead">
                <tr>
                    <th>#</th>
                    <th>Brand</th>
                    <th>Model</th>
                    <th>Year</th>
                    <th>Price</th>
                    <th>Price Difference</th>
                    <th>Photo</th>
                    <th>Link</th>
                </tr>
                </thead>
                <tbody>
                {this.state.platform.map(c =>
                    <tr key={c.pk}>
                        <td>{c.brandId}  </td>
                        <td>{c.model}</td>
                        <td>{c.year}</td>
                        <td>{c.price}</td>
                        <td>{c.price_diff}</td>
                        <td>{c.photo_link}</td>
                        <td>{c.link}</td>
                        <td>
                            {/*<button onClick={(e) => this.handleDelete(e, c.pk)}> Delete</button>*/}
                            {/*<a href={"/platform/" + c.pk}> Update</a>*/}
                        </td>
                    </tr>)}
                </tbody>
            </table>
            <button className="btn btn-primary" onClick={this.nextPage}>Next</button>
        </div>
    );
}
}
export  default  PlatformList;
