import  React, { Component } from  'react';
import './PlatformList.css'
// import  PlatformService  from './PlatformService';
// const  platformService  =  new PlatformService();

class PlatformList extends Component {

    constructor() {
        super();
        this.state = {
            platform: [],
        };
    }

    componentDidMount() {
        var url = "http://127.0.0.1:8000/api/platforml/price_diff=50"  // + this.props.product
        fetch(url, {
            mode: 'cors',
            method: 'GET',
        })
            .then(results => {return results.json()}).then(data => {

            let platform = data.results.map((plat) => {
                return (
                    <div className="AdContainer" href={plat.ad_link} >
                        <div className="imageContainer">
                            <img src={plat.photo_link} alt=""/>
                        </div>
                        <div className="titleContainer">
                            <p><b>{plat.brandId} </b> <b>{plat.model}</b></p>
                            <p> {plat.year}</p>
                        </div>
                        <div className="priceContainer">
                                <div className="price">Price: {plat.price} EUR</div>
                                <div className="price_diff">Price difference: {plat.price_diff} EUR</div>
                        </div>

                    </div>
                )
            })
            this.setState({platform: platform})
        })
    }

    render() {
        return this.state.platform
    }
}

export  default  PlatformList;
