import  React, { Component } from  'react';
import  PlatformService  from './PlatformService';
const  platformService  =  new PlatformService();


class PlatformList extends Component {

    constructor() {
        super();
        this.state = {
            platform: [],
        };
    }


    componentDidMount() {
        var url = "http://localhost:8000/api/platform/"    //+ this.props.match.params.cat

        fetch(url, {
            mode: 'cors',
            headers:{
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin':'http://localhost:3000',
            },
            method: 'GET',
        })
             .then(results => {
                return results.json();
            }).then(data => {
            let categories = data.map((cat) => {
                let link = "/category/" + cat.id;
                let linkid = "listbutton";
                if(this.props.location.pathname === link) linkid = "listbuttonSelected"
                return (
                    <div key={cat.id}>
                        <div className="name"><a id={linkid} href={link}>{cat.name}</a></div>

                    </div>
                )

            })
            this.setState({categories: categories})
        })
    }

    render() {
        return (
                <div style={{display: 'flex',  justifyContent:'center', alignItems:'top', marginLeft: '5vh', marginTop: '5px', marginRight: '5vh', fontSize: 'large'}}>
                    <Table>
                        <thead>
                        <tr>
                            <th>CATEGORIES:</th>
                        </tr>
                        </thead>
                        <tbody>
                        <tr>
                            <th><div className="categories">
                                {this.state.categories}
                            </div>
                            </th>
                        </tr>
                        </tbody>
                    </Table>
                </div>


        )
    }
}


export  default  PlatformList;
