import  React, { Component } from  'react';
import './PlatformList.css';
import Filter from './Filter';
import PropTypes from 'prop-types';
// import './pictures/car_no_image.jpg';
// import  PlatformService  from './PlatformService';
// const  platformService  =  new PlatformService();

class PlatformList extends Component {

    constructor() {
        super();
        this.state = {
            platform: [],
        };
        // const{ data: { year_min, year_max, price_min, price_max },
        //     errors,
        //     handleInput,
        //     handleSubmit,
        //     } = this.props;
    }


    onSubmit=(event)=> {
        console.log("submit")
        event.preventDefault()
        console.log(this.price_min)
    }

    changeHandler = event => {
        const { price_min, value } = event.target;
        this.setState({ [name]: value });
    };

    onYearMinChange = (event) => {
        this.year_min = event.target.value;

    }


    componentDidMount() {
        var url = "http://127.0.0.1:8000/api/platforml/price_diff=10"  // + this.props.product

        fetch(url, {
            mode: 'cors',
            method: 'GET',
        })
            .then(results => {return results.json()}).then(data => {
            let platform = data.results.map((plat) => {
                return (
                    <span key={plat.id} onClick={ e => window.location.href = plat.ad_link } >
                    <div className="AdContainer"  >
                        <div className="imageContainer">
                            <img src={plat.photo_link} alt="" />
                        </div>
                        <div className="titleContainer">
                            <p><b>{plat.brandId}  {plat.model}</b></p>
                            <p> {plat.year}</p>
                        </div>
                        <div className="priceContainer">
                                <div className="price">Price: {plat.price} EUR</div>
                                <div className="price_diff"> € {plat.price_diff} </div>
                        </div>
                    </div>
                    </span>
                )
            })
            this.setState({platform: platform})
        })

    }

    render() {
        return( <div className="FilterContainer">
                <form onSubmit={this.onSubmit} method="get">
                    <div>
                        <label htmlFor="year_min">Year from</label>
                        <input type="text" name="year_min" onChange={this.onYearMinChange} value="2010"/>
                        <label htmlFor="year_max">till</label>
                        <input name="year_max" id="year_max" value="2017"/>

                        <label htmlFor="price_min">Price from</label>
                        <input name="price_min" id="price_min" value="0"/>
                        <label htmlFor="price_max">till</label>
                        <input name="price_max" id="price_max" value="500000"/>

                        <input value="Search"  type="submit"/>
                    </div>
                </form>

                {this.state.platform}
        </div>

    )}
}

