import  React, { Component } from  'react';
// import  PlatformService  from './PlatformService';
// const  platformService  =  new PlatformService();


class PlatformList extends Component {

    constructor() {
        super();
        this.state = {
            platform: [],
        };
    }


    componentDidMount() {
        var url = "http://127.0.0.1:8000/api/platform/"  // + this.props.product
        // console.log("ProdName:", this.props.product)
        fetch(url, {
            mode: 'cors',
            method: 'GET',
        })
            .then(results => {
                return results.json();
            }).then(data => {
            let platform = data.map((plat) => {
                return (
                    <div className="userPage" key={plat.id}  >
                        <div className="fullWidth">{plat.brandId} <b>{plat.model}</b></div>
                        <div><i>{plat.year}</i></div>
                        <div>{plat.price} EUR</div>
                         <div> diff = {plat.price_diff} EUR </div>
                         <div>{plat.photo_link}</div>
                    </div>

                )
            })
            this.setState({platform: platform})
        })
    }

    render() {
        return this.state.platform
    }
}

export  default  PlatformList;
